sys_variable_value.value:
(function execute(inputs, outputs) {
    var gcc = new GoogleCloudComputeUtils();
    gcc.validateInputVariables(inputs, outputs);
    gcc.validateMandatoryVariables(inputs, ['projectid', 'zone', 'disk', 'name']);
    gcc.maptoOutputFromInput(inputs, outputs, ['projectid', 'zone', 'disk']);
    var requestBody = {
        "name": inputs.name,
        "description": inputs.description
    }
    if (inputs.labels.length > 0 && inputs["labels"][0]['key']) {
        var labels = {};
        inputs.labels.forEach(function(label) {
            labels[label['key']] = label['value'];
        })
        requestBody.labels = labels;
    }
    outputs.requestbody = JSON.stringify(requestBody);
})(inputs, outputs);

