sys_variable_value.value:
(function execute(inputs, outputs) {
    var responseBody = JSON.parse(inputs.responseBody);
    if (inputs.statusCode != 200) {
        outputs.status = 'Error';
        new GCComputeErrorHandler().errorMessageHandling(inputs, outputs);
        return;
    }
    outputs.status = 'Success';
    outputs.snapshot = responseBody;
})(inputs, outputs);

