sys_variable_value.value:
(function execute(inputs, outputs) {
    var gcc = new GoogleCloudComputeUtils();
    gcc.validateInputVariables(inputs, outputs);
    gcc.validateMandatoryVariables(inputs, ['projectid','snapshot']);
    gcc.maptoOutputFromInput(inputs, outputs, []);
})(inputs, outputs);

